import React, {useState} from 'react';

const TicTacToe = () => {
    const [board, setBoard] = useState(Array(9).fill(null));
    const [xIsNext, setXIsNext] = useState(true);
    const [winner, setWinner] = useState(null);
    const [movesCount, setMovesCount] = useState(0);

    useEffect(() => {
        // Ваши операции загрузки данных или настройки игры
        // Например, загрузка данных из API или настройка игрового поля
        setTimeout(() => {
            setIsLoading(false); // Завершаем анимацию загрузки
        }, 2000); // Длительность анимации загрузки (2 секунды)

        // Ваша логика загрузки данных может отличаться
    }, []);
    const handleClick = (i) => {
        const newBoard = board.slice();
        if (winner || newBoard[i]) {
            return;
        }
        newBoard[i] = xIsNext ? 'X' : 'O';
        setBoard(newBoard);
        setXIsNext(!xIsNext);
        setMovesCount(movesCount + 1);

        const calculatedWinner = calculateWinner(newBoard);
        if (calculatedWinner) {
            setWinner(calculatedWinner);
        } else if (movesCount + 1 === 9) {
            setWinner('Draw');
        }
    };

    const renderSquare = (i) => {
        return (
            <button className="square" onClick={() => handleClick(i)}>
                {board[i]}
            </button>
        );
    };

    let status;
    if (winner) {
        status = winner === 'Draw' ? 'It\'s a Draw!' : 'Winner: ' + winner;
    } else {
        status = 'Next player: ' + (xIsNext ? 'X' : 'O');
    }

    return (
        <div className="center-container">
            <div className="game-board">
                <div>
                    <div className="status">{status}</div>
                    <div className="board-row">
                        {renderSquare(0)}
                        {renderSquare(1)}
                        {renderSquare(2)}
                    </div>
                    <div className="board-row">
                        {renderSquare(3)}
                        {renderSquare(4)}
                        {renderSquare(5)}
                    </div>
                    <div className="board-row">
                        {renderSquare(6)}
                        {renderSquare(7)}
                        {renderSquare(8)}
                    </div>
                </div>
            </div>
        </div>
    );
};

function calculateWinner(squares) {
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
            return squares[a];
        }
    }
    return null;
}

export default TicTacToe;
