import React, { useState, useEffect } from 'react';

const calculateWinner = (squares) => {
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
            return squares[a];
        }
    }
    return null;
};

const TicTacToe = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [board, setBoard] = useState(Array(9).fill(null));
    const [xIsNext, setXIsNext] = useState(true);
    const [winner, setWinner] = useState(null);
    const [movesCount, setMovesCount] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            // Ваши операции загрузки данных или настройки игры
            // Например, загрузка данных из API или настройка игрового поля
            setTimeout(() => {
                setIsLoading(false); // Завершаем анимацию загрузки через 2 секунды
            }, 2000); // Длительность анимации загрузки (2 секунды)
        };

        fetchData();
    }, []);

    useEffect(() => {
        // Если isLoading стало false, устанавливаем другие эффекты, связанные с игрой, здесь
        if (!isLoading) {
            // Дополнительная логика после окончания загрузки
        }
    }, [isLoading]);

    const handleClick = (i) => {
        if (winner || board[i]) {
            return;
        }
        const newBoard = [...board];
        newBoard[i] = xIsNext ? 'X' : 'O';
        setBoard(newBoard);
        setXIsNext(!xIsNext);
        setMovesCount(movesCount + 1);

        const calculatedWinner = calculateWinner(newBoard);
        if (calculatedWinner) {
            setWinner(calculatedWinner);
        } else if (movesCount + 1 === 9) {
            setWinner('Draw');
        }
    };

    const renderSquare = (i) => (
        <button className="square" onClick={() => handleClick(i)}>
            {board[i]}
        </button>
    );

    const status = winner
        ? winner === 'Draw'
            ? "It's a Draw!"
            : 'Winner: ' + winner
        : 'Next player: ' + (xIsNext ? 'X' : 'O');

    return (
        <div className="center-container">
            {isLoading ? (
                <div className="preloader-container">
                    <div className="preloader"></div>
                </div>
            ) : (
                <div className="game-board">
                    <div>
                        <div className="status">{status}</div>
                        <div className="board-row">
                            {renderSquare(0)}
                            {renderSquare(1)}
                            {renderSquare(2)}
                        </div>
                        <div className="board-row">
                            {renderSquare(3)}
                            {renderSquare(4)}
                            {renderSquare(5)}
                        </div>
                        <div className="board-row">
                            {renderSquare(6)}
                            {renderSquare(7)}
                            {renderSquare(8)}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default TicTacToe;
