import React, { useState, useEffect } from 'react';
import GameBoard from './GameBoard';
import StartButton from './StartButton';
import WinnerResults from './WinnerResults'; // Новый компонент для отображения результатов победителя

function calculateWinner(squares) {
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
            return squares[a];
        }
    }
    return null;
}

function TicTacToe() {
    const [isLoading, setIsLoading] = useState(true);
    const [isGameStarted, setIsGameStarted] = useState(false);
    const [board, setBoard] = useState(Array(9).fill(null));
    const [xIsNext, setXIsNext] = useState(true);
    const [winner, setWinner] = useState(null);
    const [movesCount, setMovesCount] = useState(0);
    const [showWinnerPopup, setShowWinnerPopup] = useState(false); // Добавлено состояние для управления попапом

    useEffect(() => {
        const fetchData = async () => {
            // Ваши операции загрузки данных или настройки игры
            setTimeout(() => {
                setIsLoading(false);
            }, 2000);
        };

        fetchData();
    }, []);

    const handleClick = (i) => {
        if (winner || board[i]) {
            return;
        }
        const newBoard = [...board];
        newBoard[i] = xIsNext ? 'X' : 'O';
        setBoard(newBoard);
        setXIsNext(!xIsNext);
        setMovesCount(movesCount + 1);

        const calculatedWinner = calculateWinner(newBoard);
        if (calculatedWinner) {
            setWinner(calculatedWinner);
            setShowWinnerPopup(true); // Показать попап при победе
        } else if (movesCount + 1 === 9) {
            setWinner('Draw');
            setShowWinnerPopup(true); // Показать попап при ничьей
        }
    };

    const startGame = () => {
        setIsGameStarted(true);
        setBoard(Array(9).fill(null));
        setXIsNext(true);
        setWinner(null);
        setMovesCount(0);
        setShowWinnerPopup(false); // Скрыть попап при начале новой игры
    };

    let gameContent;

    if (isLoading) {
        gameContent = (
            <div className="preloader-container">
                <div className="preloader"></div>
            </div>
        );
    } else if (isGameStarted) {
        const status = winner
            ? winner === 'Draw'
                ? "It's a Draw!"
                : 'Winner: ' + winner
            : 'Next player: ' + (xIsNext ? 'X' : 'O');

        gameContent = (
            <div>
                <GameBoard
                    board={board}
                    handleClick={handleClick}
                    status={status}
                    renderSquare={(i) => (
                        <button className="square" onClick={() => handleClick(i)}>
                            {board[i]}
                        </button>
                    )}
                />
                {showWinnerPopup && (
                    <WinnerResults winner={winner} startGame={startGame} />
                )}
            </div>
        );
    } else {
        gameContent = (
            <div>
                <StartButton onStartClick={startGame} />
            </div>
        );
    }

    return (
        <div className="center-container">
            <div className="game-board">{gameContent}</div>
        </div>
    );
}

export default TicTacToe;
