import React from 'react';
import style from '../../styles/GameBoard.module.scss';

function GameBoard(props) {
    const boardSize = 50;

    const renderSquare = (i) => (
        <button className={style.square} onClick={() => props.onClick(i)}>
            {props.squares[i]}
        </button>
    );

    const renderBoardRow = (rowIndex) => {
        const row = [];
        for (let j = 0; j < boardSize; j++) {
            row.push(renderSquare(rowIndex * boardSize + j));
        }
        return (
            <div key={rowIndex} className={style.boardRow}>
                {row}
            </div>
        );
    };

    const renderBoard = () => {
        const board = [];
        for (let i = 0; i < boardSize; i++) {
            board.push(renderBoardRow(i));
        }
        return board;
    };

    return <div>{renderBoard()}</div>;
}

export default GameBoard;
