import React, { useState, useEffect } from 'react';
import GameBoard from './GameBoard';
import StartButton from './StartButton';

function calculateWinner(squares) {
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
            return squares[a];
        }
    }
    return null;
}

function TicTacToe() {
    const [isLoading, setIsLoading] = useState(true);
    const [isGameStarted, setIsGameStarted] = useState(false);
    const [board, setBoard] = useState(Array(9).fill(null));
    const [xIsNext, setXIsNext] = useState(true);
    const [winner, setWinner] = useState(null);
    const [movesCount, setMovesCount] = useState(0);
    const [gameOver, setGameOver] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            // Ваши операции загрузки данных или настройки игры
            setTimeout(() => {
                setIsLoading(false);
            }, 2000);
        };

        fetchData();
    }, []);

    useEffect(() => {
        if (!isLoading) {
            // Дополнительная логика после окончания загрузки
        }
    }, [isLoading]);

    const handleClick = (i) => {
        if (winner || board[i] || gameOver) {
            return;
        }
        const newBoard = [...board];
        newBoard[i] = xIsNext ? 'X' : 'O';
        setBoard(newBoard);
        setXIsNext(!xIsNext);
        setMovesCount(movesCount + 1);

        const calculatedWinner = calculateWinner(newBoard);
        if (calculatedWinner) {
            setWinner(calculatedWinner);
            setGameOver(true);
        } else if (movesCount + 1 === 9) {
            setWinner('Draw');
            setGameOver(true);
        }
    };

    const startGame = () => {
        setIsGameStarted(true);
        setBoard(Array(9).fill(null));
        setXIsNext(true);
        setWinner(null);
        setMovesCount(0);
        setGameOver(false);
    };

    const renderSquare = (i) => (
        <button className="square" onClick={() => handleClick(i)}>
            {board[i]}
        </button>
    );

    const status = winner
        ? winner === 'Draw'
            ? "It's a Draw!"
            : 'Winner: ' + winner
        : 'Next player: ' + (xIsNext ? 'X' : 'O');

    return (
        <div className="center-container">
            {isLoading ? (
                <div className="preloader-container">
                    <div className="preloader"></div>
                </div>
            ) : (
                <div className="game-board">
                    {isGameStarted ? (
                        <div>
                            <GameBoard
                                board={board}
                                handleClick={handleClick}
                                status={status}
                                renderSquare={renderSquare}
                            />
                            <button onClick={startGame}>Начать новую игру</button>
                        </div>
                    ) : (
                        <StartButton onStartClick={startGame} />
                    )}
                </div>
            )}
        </div>
    );
}

export default TicTacToe;
